			WHEN Dept_Alias IS NOT NULL THEN Dept_Alias 		   
		END AS Last_Alias,
		CASE 
			WHEN SubSect_Name IS NOT NULL THEN 3
		   WHEN Sect_Name IS NOT NULL THEN 2
			WHEN Dept_Name IS NOT NULL THEN 1 		   
		END AS Level_Action,		
		
      COUNT(qform_karyawan_aktif.NIK) AS Count_Karyawan,
      
      /* STAFF */
      SUM(CASE WHEN qform_karyawan_aktif.Jabatan = 'STAFF' AND (qform_karyawan_aktif.Employee_Status = 'ST' OR qform_karyawan_aktif.Employee_Status = 'SK' OR qform_karyawan_aktif.Employee_Status = 'S') THEN 1 ELSE 0 END) AS Staff,
      SUM(CASE WHEN qform_karyawan_aktif.Jabatan = 'Sub Section Head' AND (qform_karyawan_aktif.Employee_Status = 'ST' OR qform_karyawan_aktif.Employee_Status = 'SK' OR qform_karyawan_aktif.Employee_Status = 'S') THEN 1 ELSE 0 END) AS SubSectHead,
      SUM(CASE WHEN qform_karyawan_aktif.Jabatan = 'Section Head' AND (qform_karyawan_aktif.Employee_Status = 'ST' OR qform_karyawan_aktif.Employee_Status = 'SK' OR qform_karyawan_aktif.Employee_Status = 'S') THEN 1 ELSE 0 END) AS SectHead,
      SUM(CASE WHEN qform_karyawan_aktif.Jabatan = 'Deputy Department Head' AND (qform_karyawan_aktif.Employee_Status = 'ST' OR qform_karyawan_aktif.Employee_Status = 'SK' OR qform_karyawan_aktif.Employee_Status = 'S') THEN 1 ELSE 0 END) AS DptyDeptHead,
      SUM(CASE WHEN qform_karyawan_aktif.Jabatan = 'Department Head' AND (qform_karyawan_aktif.Employee_Status = 'ST' OR qform_karyawan_aktif.Employee_Status = 'SK' OR qform_karyawan_aktif.Employee_Status = 'S') THEN 1 ELSE 0 END) AS DeptHead,
      SUM(CASE WHEN qform_karyawan_aktif.Jabatan = 'Deputy Division Head' AND (qform_karyawan_aktif.Employee_Status = 'ST' OR qform_karyawan_aktif.Employee_Status = 'SK' OR qform_karyawan_aktif.Employee_Status = 'S') THEN 1 ELSE 0 END) AS DptyDivHead,
      SUM(CASE WHEN qform_karyawan_aktif.Jabatan = 'Division Head' AND (qform_karyawan_aktif.Employee_Status = 'ST' OR qform_karyawan_aktif.Employee_Status = 'SK' OR qform_karyawan_aktif.Employee_Status = 'S') THEN 1 ELSE 0 END) AS DivHead,
      SUM(CASE WHEN qform_karyawan_aktif.Jabatan = 'Director' AND (qform_karyawan_aktif.Employee_Status = 'ST' OR qform_karyawan_aktif.Employee_Status = 'SK' OR qform_karyawan_aktif.Employee_Status = 'S') THEN 1 ELSE 0 END) AS Director,

      /* KARYAWAN NS */
      SUM(CASE WHEN qform_karyawan_aktif.Jabatan = 'Leader' AND (qform_karyawan_aktif.Employee_Status = 'NS' OR qform_karyawan_aktif.Employee_Status = 'NST') THEN 1 ELSE 0 END) AS NS_Leader,
      SUM(CASE WHEN qform_karyawan_aktif.Jabatan = 'Operator' AND (qform_karyawan_aktif.Employee_Status = 'NS' OR qform_karyawan_aktif.Employee_Status = 'NST') THEN 1 ELSE 0 END) AS NS_Operator,
      SUM(CASE WHEN qform_karyawan_aktif.Jabatan = 'Sub Section Head' AND (qform_karyawan_aktif.Employee_Status = 'NS' OR qform_karyawan_aktif.Employee_Status = 'NST') THEN 1 ELSE 0 END) AS NS_SubSectHead,
      SUM(CASE WHEN qform_karyawan_aktif.Jabatan = 'Administrator' AND (qform_karyawan_aktif.Employee_Status = 'NS' OR qform_karyawan_aktif.Employee_Status = 'NST') THEN 1 ELSE 0 END) AS NS_Admin,

      /* KARYAWAN NK */
      SUM(CASE WHEN qform_karyawan_aktif.Jabatan = 'Leader' AND (qform_karyawan_aktif.Employee_Status = 'NK' OR qform_karyawan_aktif.Employee_Status = 'NSK') THEN 1 ELSE 0 END) AS NK_Leader,
      SUM(CASE WHEN qform_karyawan_aktif.Jabatan = 'Operator' AND (qform_karyawan_aktif.Employee_Status = 'NK' OR qform_karyawan_aktif.Employee_Status = 'NSK') THEN 1 ELSE 0 END) AS NK_Operator,
      SUM(CASE WHEN qform_karyawan_aktif.Jabatan = 'Administrator' AND (qform_karyawan_aktif.Employee_Status = 'NK' OR qform_karyawan_aktif.Employee_Status = 'NSK') THEN 1 ELSE 0 END) AS NK_Admin,

      /* PENDIDIKAN */
      SUM(CASE WHEN Pend_Terakhir = 'SD' THEN 1 ELSE 0 END) AS SD,
      SUM(CASE WHEN Pend_Terakhir = 'SLTP' OR Pend_Terakhir = 'SMP' THEN 1 ELSE 0 END) AS SLTP,
      SUM(CASE WHEN Pend_Terakhir = 'SMA' OR Pend_Terakhir = 'SMK' OR Pend_Terakhir = 'SMEA' OR Pend_Terakhir = 'STM' THEN 1 ELSE 0 END) AS SLTA,
      SUM(CASE WHEN Pend_Terakhir = 'D1' OR Pend_Terakhir = 'D2' OR Pend_Terakhir = 'D3' THEN 1 ELSE 0 END) AS D1_3,
      SUM(CASE WHEN Pend_Terakhir = 'D4' THEN 1 ELSE 0 END) AS D4,
      SUM(CASE WHEN Pend_Terakhir = 'S1' THEN 1 ELSE 0 END) AS S1,
      SUM(CASE WHEN Pend_Terakhir = 'S2' THEN 1 ELSE 0 END) AS S2,
      
      /* USIA */
      SUM(CASE WHEN DATE_FORMAT(curdate(),'%Y') - DATE_FORMAT(qform_karyawan_aktif.Tgl_Lahir,'%Y') >= 17
        AND DATE_FORMAT(curdate(),'%Y') - DATE_FORMAT(qform_karyawan_aktif.Tgl_Lahir,'%Y') <= 25
        THEN 1 ELSE 0 END) AS Usia17_25,
      SUM(CASE WHEN DATE_FORMAT(curdate(),'%Y') - DATE_FORMAT(qform_karyawan_aktif.Tgl_Lahir,'%Y') >= 26
        AND DATE_FORMAT(curdate(),'%Y') - DATE_FORMAT(qform_karyawan_aktif.Tgl_Lahir,'%Y') <= 50
        THEN 1 ELSE 0 END) AS Usia26_50,
      SUM(CASE WHEN DATE_FORMAT(curdate(),'%Y') - DATE_FORMAT(qform_karyawan_aktif.Tgl_Lahir,'%Y') >= 51
        THEN 1 ELSE 0 END) AS Usia51,
      
      /* JENIS KELAMIN */
      SUM(CASE qform_karyawan_aktif.Jenis_Kelamin WHEN 'L' THEN 1 ELSE 0 END) AS L,
      SUM(CASE qform_karyawan_aktif.Jenis_Kelamin WHEN 'P' THEN 1 ELSE 0 END) AS P,
      
      /* STATUS KAWIN */
      SUM(CASE WHEN qform_karyawan_aktif.Status_Kawin = '' OR qform_karyawan_aktif.Status_Kawin = '0' THEN 1 ELSE 0 END) AS Kawin,
      SUM(CASE WHEN qform_karyawan_aktif.Status_Kawin = '1' THEN 1 ELSE 0 END) AS TidakKawin

From qform_karyawan_aktif
GROUP BY qform_karyawan_aktif.Dept_Name, qform_karyawan_aktif.Sect_Name, qform_karyawan_aktif.SubSect_Name ;

